from django.urls import pathfrom phone.views import *from django.conf import settingsfrom django.conf.urls.static import staticurlpatterns = [    path('', IndexListView.as_view(), name='index'),    path('index/<int:pk>/', PhoneUpdateView.as_view(), name='update'),    path('addpage/', AddPage.as_view(), name='add_page'),    path('login/', LoginUser.as_view(), name='login'),    path('logout/', logout_user, name='logout'),    path('post/<int:cat_id>/', ShowPost.as_view(), name='post'),    path('category/<int:cat_id>/', PhoneCategory.as_view(), name='category'),]if settings.DEBUG:    urlpatterns += static(settings.MEDIA_URL, document_root=settings.MEDIA_ROOT)